import React, { ChangeEvent, useState } from 'react'
import InfiniteScroll from 'react-infinite-scroller';
import jobConfig from '../config/dashboards/jobConfig'
import { useQuery, useInfiniteQuery, useMutation } from 'react-query'
import { job } from '../config/dashboards/jobConfig'

export default function JobDashboard() {
  const [fetchOptions, setFetchOptions] = useState({
    itemsPerPage: 10, sortOptions: {
      sortBy: 'isUnassigned',
      sortOrder: 'desc'
    }
  })

  // Get an overview of the data with some statistics:
  //booking grade
  //office city
  //skills
  //industry
  // Drill down into subsets by means of filtering and/or sorting

  //const sortField=useMutation((field))


  const { data, status, hasNextPage, fetchNextPage, isSuccess
    //@ts-ignore
  } = useInfiniteQuery(["InfiniteQueryData", fetchOptions], ({ pageParam = 1 }) => jobConfig.fetch(pageParam, fetchOptions), {

    getNextPageParam: (lastPage, allPages) => {
      const nextPage = allPages.length + 1
      return nextPage
    }
  })

  const handleCheckboxChange = (event: ChangeEvent<HTMLInputElement>) => {
    console.log(event.target.value)
  }

  console.log(data)
  //@ts-ignore
  const sortData = (newFetchOptions) => {
    if (fetchOptions.sortOptions.sortBy !== newFetchOptions.sortOptions.sortBy) {
      setFetchOptions(newFetchOptions)
    } else {
      newFetchOptions.sortOptions.sortOrder = fetchOptions.sortOptions.sortOrder === 'asc' ? 'desc' : 'asc'
      console.log(newFetchOptions.sortOptions.sortOrder)
      setFetchOptions(newFetchOptions)
    }

  }

  //onClick={() => setFetchOptions({ ...fetchOptions, sortOptions: { sortBy: fields[fieldString].name, sortOrder: 'asc' } })}
  const fields = jobConfig.fields

  if (status === 'loading') return <h1>Loading...</h1>
  if (status === 'error') return <h1>There has been an error</h1>
  return (
    <>
      <div>
        <h5>Main insights:</h5>
        {isSuccess && data.pages.length}
        <ul>
          <li><span>Data size:</span></li>
          <li><span>Booking grade:</span></li>
          <li><span>Office city:</span></li>
          <li><span>Skills:</span></li>
          <li><span>Industry:</span></li>
        </ul>
      </div>
      <h6>
        Filters
      </h6>
      <div>
        <label htmlFor="isUnassigned">Unassigned</label>
        <input name="isUnassigned" onChange={handleCheckboxChange} type="checkbox"></input>
      </div>
      {/*@ts-ignore*/}
      < InfiniteScroll loadMore={fetchNextPage} hasMore={hasNextPage} >
        <table className="table table-hover">
          <thead>
            <tr>
              {Object.keys(fields)
                .map(fieldString =>
                  <th scope="col">
                    <div className='d-flex'>{fields[fieldString].label}
                      <i className="fa fa-fw fa-sort"
                        onClick={() => sortData({ ...fetchOptions, sortOptions: { sortBy: fields[fieldString].name } })}
                      >
                      </i>
                    </div>
                  </th>)}
            </tr>
          </thead>
          <tbody>
            {isSuccess &&
              //@ts-ignore
              data.pages.map((page) => {
                return (page as job[]).map((e) => {
                  return <tr>
                    <td >{e.id}</td>
                    <td>{e.originalId}</td>
                    <td>{e.talentId}</td>
                    <td>{e.talentName}</td>
                    <td>{e.talentGrade}</td>
                    <td>{e.bookingGrade}</td>
                    <td>{e.operatingUnit}</td>
                    <td>{e.officeCity}</td>
                    <td>{e.officePostalCode}</td>
                    <td>{e.jobManagerName}</td>
                    <td>{e.jobManagerId}</td>
                    <td>{e.totalHours}</td>
                    <td>{e.startDate?.toString()}</td>
                    <td>{e.endDate?.toString()}</td>
                    <td>{e.clientName}</td>
                    <td>{e.clientId}</td>
                    <td>{e.industry}</td>
                    <td>{e.requiredSkills?.map(skill => <div>
                      <span>Name: {skill.name}</span>
                      <span>Category: {skill.category}</span>
                    </div>)}</td>
                    <td>{e.optionalSkills?.map(skill => <div>
                      <span>Name: {skill.name}</span>
                      <span>Category: {skill.category}</span>
                    </div>)}</td>
                    <td>{e.isUnassigned ? 'true' : 'false'}</td>
                  </tr>
                });
              }
              )}</tbody>
        </table>
      </InfiniteScroll >
    </>
  )
}
