import React, { ChangeEvent, useState, useContext } from 'react'
import { JobContext } from '../context/jobContext'
import { UPDATE_FILTERS } from '../reducers/jobReducerTypes'

export default function FilterGroup() {
    //@ts-ignore
    const [state, dispatch] = useContext(JobContext)

    const handleCheckboxChange = (event: ChangeEvent<HTMLInputElement>) => {
        if (event.target.checked === true) {
            dispatch({ type: UPDATE_FILTERS, payload: [...state.filterOptions, { filterBy: event.target.name, filterParam: 'true' }] })
            console.log(state)
        } else {
            //@ts-ignore
            dispatch({ type: UPDATE_FILTERS, payload: state.filterOptions.filter(options => options?.filterBy !== event.target.name) })
        }
    }

    const handleSearchChange = (event: ChangeEvent<HTMLInputElement>) => {
        //@ts-ignore
        console.log(state)
        //@ts-ignore
        if (!state.filterOptions.some(filter => filter.filterBy === 'q')) {
            //@ts-ignore
            dispatch({ type: UPDATE_FILTERS, payload: [...state.filterOptions, { filterBy: 'q', filterParam: event.target.value }] })
        } else {
            //@ts-ignore
            dispatch({ type: UPDATE_FILTERS, payload: state.filterOptions.filter(options => options?.filterBy !== 'q') })
            console.log(state);

        }
    }

    return (
        <div><h6 >
            Filters
        </h6>
            <div className='d-flex justify-content-center'>
                <div>
                    <label htmlFor="isUnassigned">Unassigned</label>
                    <input name="isUnassigned" onChange={handleCheckboxChange} type="checkbox"></input>
                </div>
                <div>
                    <input name='textSearch' type="text" placeholder='Search' onChange={handleSearchChange}></input>
                </div>
            </div>
        </div>
    )
}
