//@ts-ignore
export const reducer = (state, action) => {
    switch (action.type) {
        case "updateFilters":
            return {
                ...state,
                active: !state.active
            }
        case "updateSorting":
            return {
                ...state,
                sortOptions: action.payload
            }

        default:
            return state
    }
}

export const initialState = {
    itemsPerPage: 10, sortOptions: {
        sortBy: 'id',
        sortOrder: 'asc'
    }, filterOptions: []
}
