import { ReducerAction, ReducerState } from "react"
import { FetchOptions } from "../../api/jobs-api"
import { UPDATE_SORTING, UPDATE_FILTERS } from "./jobReducerTypes"

export const initialState: FetchOptions = {
    itemsPerPage: 10, sortOptions: {
        sortBy: 'id',
        sortOrder: 'asc'
    }, filterOptions: [
        {
            filterBy: 'q',
            filterParam: ''
        }
    ]
}

//@ts-ignore
export const reducer = (state = initialState, action) => {
    switch (action.type) {

        case UPDATE_FILTERS:
            return {
                ...state,
                filterOptions: action.payload
            }

        case UPDATE_SORTING:
            return {
                ...state,
                sortOptions: action.payload
            }

        default:
            return state
    }
}
