//query for pagination and sorting

interface FetchOptions {
    itemsPerPage: number;
    sortOptions?: SortOptions
}
type SortOptions = {
    sortBy: string;
    sortOrder: string
}

export const reactiveFetchJobs = async (page = 0, fetchOptions: FetchOptions) => {
    return fetch(`http://localhost:9000/jobs?_page=${page}&_limit=${fetchOptions.limit}&_sort=${fetchOptions.sortBy}&_order=desc`
        , {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        }
    )
        .then(function (response) {
            console.log(response)
            return response.json();
        })
        .then(function (result) {
            console.log(result);
            return result
        });
}


//getAllJobs
