import React, { useContext } from 'react'
import { useInfiniteQuery } from 'react-query'
import { JobContext } from '../context/jobContext';
import InfiniteScroll from 'react-infinite-scroller';
import jobConfig from '../config/dashboards/jobConfig'
import Stats from '../ui/Stats';
import Table from '../ui/Table';
import FilterGroup from '../ui/FilterGroup';

const JobDashboard = () => {
  //@ts-ignore
  const [state] = useContext(JobContext)

  const { status, hasNextPage, fetchNextPage, isSuccess
    //@ts-ignore
  } = useInfiniteQuery(["InfiniteQueryData", state], ({ pageParam = 1 }) => jobConfig.fetch(pageParam, state), {
    getNextPageParam: (lastPage, allPages) => {
      const nextPage = allPages.length + 1
      return nextPage
    }
  })

  return (
    <div>
      <Stats />
      <FilterGroup />
      {status === 'loading' && <h1>Loading...</h1>}
      {status === 'error' && <h1>There has been an error</h1>}
      <Table />
    </div>
  )
}

export default JobDashboard
