import React from 'react'
import InfiniteScroll from 'react-infinite-scroller';
import jobConfig from '../config/dashboards/jobConfig'
import { useQuery, useInfiniteQuery } from 'react-query'
import { job } from '../config/dashboards/jobConfig'

export default function JobDashboard() {

  const LIMIT = 10;
  // Get an overview of the data with some statistics:
  //booking grade
  //office city
  //skills
  //industry
  // Drill down into subsets by means of filtering and/or sorting


  //data.pages[0] as job[]).map(e =>


  //@ts-ignore
  const { data, status, page, hasNextPage, fetchNextPage, isFetchingNextPage, isSuccess, isLoading
    //@ts-ignore
  } = useInfiniteQuery("fetchData", ({ pageParam = 1 }) => jobConfig.fetch(pageParam, LIMIT), {
    //@ts-ignore
    getNextPageParam: (lastPage, allPages) => {
      const nextPage = allPages.length + 1
      return nextPage
    }
  })
  console.log(data)

  const fields = jobConfig.fields

  if (status === 'loading') return <h1>Loading...</h1>
  if (status === 'error') return <h1>There has been an error</h1>
  return (
    <table className="table table-hover">
      <thead>
        <tr>
          {Object.keys(fields).map(fieldString => <th scope="col">{fields[fieldString].label}</th>)}
        </tr>
      </thead>

      { /* @ts-ignore */}
      <InfiniteScroll loadMore={fetchNextPage} hasMore={hasNextPage} >
        <tbody>
          {isSuccess &&
            //@ts-ignore
            data.pages.map((page) => {
              return (page as job[]).map((e) => {
                return <tr>
                  <td>{e.id}</td>
                  <td>{e.originalId}</td>
                  <td>{e.talentId}</td>
                  <td>{e.talentName}</td>
                  <td>{e.talentGrade}</td>
                  <td>{e.bookingGrade}</td>
                  <td>{e.operatingUnit}</td>
                  <td>{e.officeCity}</td>
                  <td>{e.officePostalCode}</td>
                  <td>{e.jobManagerName}</td>
                  <td>{e.jobManagerId}</td>
                  <td>{e.totalHours}</td>
                  <td>{e.startDate?.toString()}</td>
                  <td>{e.endDate?.toString()}</td>
                  <td>{e.clientName}</td>
                  <td>{e.clientId}</td>
                  <td>{e.industry}</td>
                  <td>{e.requiredSkills?.map(skill => <div>
                    <span>Name: {skill.name}</span>
                    <span>Category: {skill.category}</span>
                  </div>)}</td>
                  <td>{e.optionalSkills?.map(skill => <div>
                    <span>Name: {skill.name}</span>
                    <span>Category: {skill.category}</span>
                  </div>)}</td>
                  <td>{e.isUnassigned ? 'true' : 'false'}</td>
                </tr>
              });
            }
            )}</tbody>
      </InfiniteScroll>

    </table>
  )
}
