import { ReducerAction, ReducerState } from "react"

export const initialState = {
    itemsPerPage: 10, sortOptions: {
        sortBy: 'id',
        sortOrder: 'asc'
    }, filterOptions: []
}

//@ts-ignore
export const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "updateCheckboxFilter":
            return {
                ...state,
                filterOptions: action.payload
            }
        case "updateSearch":
            return {
                ...state,
                filterOptions: [...state.filterOptions, action.payload]
            }
        case "updateSorting":
            return {
                ...state,
                sortOptions: action.payload
            }

        default:
            return state
    }
}
