import React from 'react'
import { useQuery } from 'react-query'
import { EntityConfig } from '../config/main/schema'

type StatsProps = {
    entityConfig: EntityConfig
}

export default function Stats({ entityConfig }: StatsProps) {
    const { data, status, isSuccess } = useQuery(entityConfig.normalQueryName, entityConfig.fetchAll)

    const { fields } = entityConfig
    const filterBoolean = (field: string, value: boolean) => {
        //@ts-ignore
        return data.filter((e) =>
            e[field] === value
        )
    }
    const getSet = (field: string) => {
        const arr: Iterable<Array<string>> | null | undefined = []
        //@ts-ignore
        data.map(e => {
            if (e[field]) {
                //@ts-ignore
                arr.push(e[field])
            }
        }
        )
        return Array.from(new Set(arr))
    }
    const getMostFrequent = (arr: string[]) => {
        const filteredArray = arr.filter(e => e !== undefined)
        if (filteredArray.length > 0) {
            const hashmap = filteredArray.reduce((acc, val) => {
                //@ts-ignore
                (acc)[val] = (acc[val] || 0) + 1
                return acc
            }, {})
            //@ts-ignore
            return Object.keys(hashmap).reduce((a, b) => hashmap[a] > hashmap[b] ? a : b)
        }
    }

    const getEarliestDate = (dates: Array<Date>) => {
        //@ts-ignore
        return dates.sort(function (a, b) {
            //@ts-ignore
            return Date.parse(a) > Date.parse(b);
        });
    }
    const getMostBy = (mostFrequent: string, by: string) => {

    }

    const getTop = (targetField: string) => {

    }

    const getPercentage = () => {

    }



    const getMostRequiredSkillsByRole = (role: string) => {
        let skillsArray: string[] = [];
        (data as unknown[]).filter((e) => {
            //@ts-ignore
            return e.bookingGrade === role
        }).map((filteredEl) => {
            //@ts-ignore
            filteredEl?.requiredSkills?.forEach((element) => {
                return skillsArray.push((element.name))
            });

        }

        )
        return getMostFrequent(skillsArray)
    }

    const getTopClient = () => {
        let clientArray: string[] = [];

        (data as unknown[]).map(e => {
            //@ts-ignore

            return clientArray.push(e.clientId)
        });
        return getMostFrequent(clientArray)
    }

    const getOfficeWithMostJobsByTopClient = () => {
        const client = getTopClient()
        let officeCityArray: string[] = [];

        (data as unknown[]).filter((e) => {
            //@ts-ignore

            return e.clientId === client
        }).map(filteredEl => {
            //@ts-ignore

            return filteredEl.officeCity && officeCityArray.push(filteredEl.officeCity)
        });
        return getMostFrequent(officeCityArray)
    }

    const getPercentageOfJobsByIndustry = (industry: string) => {
        const filteredData = (data as unknown[]).filter((e) => {
            //@ts-ignore

            return e.industry === industry
        })
        return filteredData.length / (data as unknown[]).length * 100
    }

    if (status === 'loading') return <h6>{'Loading stats...'}</h6>
    if (status === 'error') return <h6>{'Error loading stats'}</h6>
    return (
        <div className="d-flex justify-content-between p-5">
            <div className='bg-secondary rounded'>
                <h5>
                    {'Data insights:'}
                </h5>

                <ul className="list-group">
                    <li key={1} className="list-group-item">
                        <span>{'Data size: '} </span>
                        <strong>{isSuccess && (data as unknown[]).length} </strong>
                    </li>
                    <li key={2} className="list-group-item">
                        <span >{'Number of cities: '} </span>
                        <strong>{getSet('officeCity').length}</strong>
                    </li>
                    <li key={3} className="list-group-item">
                        <span>{'Unassigned jobs: '} </span>
                        <strong>{filterBoolean('isUnassigned', true).length}</strong>
                    </li>
                    <li key={4} className="list-group-item">
                        <span>{'Number of industries: '} </span>
                        <strong>{getSet('industry').length}</strong>
                    </li>
                </ul>
            </div>
            <div className='bg-warning rounded'>
                <h5>
                    {'Top Priority:'}
                </h5>

                <ul className="list-group">
                    <li key={1} className="list-group-item">
                        <span>{'Item with closest start date: '} </span>
                        <strong>{getEarliestDate(getSet('startDate'))} </strong>
                    </li>
                    <li key={2} className="list-group-item">
                        <span >{'Most in-demand skills for senior managers: '} </span>
                        <strong>{getMostRequiredSkillsByRole('Senior Manager')}</strong>
                    </li>
                    <li key={3} className="list-group-item">
                        <span>{'Office city with the most jobs from the top client: '} </span>
                        <strong>{getOfficeWithMostJobsByTopClient()}</strong>
                    </li>
                    <li key={4} className="list-group-item">
                        <span>{'Percentage of jobs in Low Tech: '} </span>
                        <strong>{isSuccess && getPercentageOfJobsByIndustry("Low technology") + '%'}</strong>
                    </li>
                </ul>
            </div>
            <div className='bg-secondary rounded'>
                <h5>
                    {'Did you know?'}
                </h5>

                <ul className="list-group">
                    <li key={1} className="list-group-item">
                        <span>{'Data size: '} </span>
                        <strong>{ } </strong>
                    </li>
                    <li key={2} className="list-group-item">
                        <span >{'Most in-demand skills for senior managers: '} </span>
                        <strong>{ }</strong>
                    </li>
                    <li key={3} className="list-group-item">
                        <span>{'Office city with the most jobs from the top client: '} </span>
                        <strong>{ }</strong>
                    </li>
                    <li key={4} className="list-group-item">
                        <span>{'Percentage of jobs in Low Tech: '} </span>
                        <strong>{isSuccess && getPercentageOfJobsByIndustry("Low technology") + '%'}</strong>
                    </li>
                </ul>
            </div>
        </div>

    )
}
