import React, { ChangeEvent, useContext, useState } from 'react'
import { JobContext } from '../context/jobContext'
import { UPDATE_FILTERS } from '../reducers/jobReducerTypes'
import { FetchOptions } from 'react-query/types/core/query'
import jobConfig from '../config/dashboards/jobConfig'
import { FilterOptions } from '../../api/jobs-api'

export default function FilterGroup() {
    const [searchBy, setSearchBy] = useState('q')
    //@ts-ignore
    const [state, dispatch] = useContext(JobContext)

    const fields = jobConfig.fields

    const handleCheckboxChange = (event: ChangeEvent<HTMLInputElement>) => {
        if (event.target.checked === true) {
            dispatch({ type: UPDATE_FILTERS, payload: [...state.filterOptions, { filterBy: event.target.name, filterParam: 'true' }] })
            console.log(state)
        } else {
            dispatch({ type: UPDATE_FILTERS, payload: state.filterOptions.filter((options: FilterOptions) => options?.filterBy !== event.target.name) })
        }
    }

    const handleSearchChange = (event: ChangeEvent<HTMLInputElement>) => {
        console.log(state)
        console.log(searchBy)
        //@ts-ignore
        if (!(state as FetchOptions)?.filterOptions.some(filter => filter.filterBy === searchBy)) {
            //@ts-ignore
            dispatch({ type: UPDATE_FILTERS, payload: [...state.filterOptions, { filterBy: searchBy, filterParam: event.target.value }] })
        } else {
            //@ts-ignore
            dispatch({ type: UPDATE_FILTERS, payload: state.filterOptions.filter(options => options?.filterBy !== searchBy) })

        }
    }

    const onSelectChange = (event: ChangeEvent<HTMLSelectElement>) => {
        //@ts-ignore
        const index = state.filterOptions.findIndex((el) => el.filterBy === searchBy)
        let newFilterOptions = state.filterOptions
        newFilterOptions[index].filterBy = event.target.value
        dispatch({ type: UPDATE_FILTERS, payload: newFilterOptions })
        setSearchBy(event.target.value)
    }

    return (
        <div>
            <h5>
                Filters
            </h5>
            <div className='d-flex justify-content-center'>
                <div className='d-flex align-items-center'>
                    <label className='p-1' htmlFor="isUnassigned">Unassigned</label>
                    <input name="isUnassigned" onChange={handleCheckboxChange} type="checkbox" />
                </div>
                <div className='m-3'>
                    <input name='textSearch' type="text" placeholder='Search by' onChange={handleSearchChange} />
                </div>
                <div className="d-flex align-items-center">
                    <select onChange={onSelectChange} className="form-select" aria-label="Default select example">
                        <option value="q" selected>All</option>
                        {Object.keys(fields)
                            .map(fieldString => fields[fieldString].type !== 'boolean' && fields[fieldString].type !== 'keyValueArray' &&
                                <option value={fields[fieldString].name}>
                                    {fields[fieldString].label}
                                </option>)
                        }
                    </select>
                </div>
            </div>
        </div>
    )
}
