import React from 'react'
import { useQuery, UseQueryOptions } from 'react-query'
import { job } from '../config/dashboards/jobConfig'
import jobConfig from '../config/dashboards/jobConfig'

//most repeated string in an array
export default function Stats() {

    const getMostRepeated = (element: any, field: string) => {


    }

    const getMostFrequentSkillsByRole = (role: string) => {
        const filteredData = (data as job[]).filter((e) => {
            return e.bookingGrade === role
            //array with only manager and skills
        })
        console.log(filteredData)

    }
    const getOfficeWithMostJobsByClient = (client: string) => {
        const filteredData = (data as job[]).filter((e) => {
            return e.clientName === client
        })

    }

    const getPercentageOfJobsByIndustry = (industry: string) => {
        const filteredData = (data as job[]).filter((e) => {
            return e.industry === industry
        })
        return filteredData.length / (data as job[]).length * 100
    }





    const { data, status, isSuccess } = useQuery("fetchAllData", jobConfig.fetchAll)
    if (status === 'loading') return <h1>Loading stats...</h1>
    if (status === 'error') return <h1>Error loading stats</h1>
    return (

        <div>
            <h5>Main insights:</h5>
            <ul className="list-group">
                {/*@ts-ignore */}
                <li className="list-group-item"><span>Data size: {isSuccess && data?.length} </span></li>

                <li className="list-group-item">
                    {/*@ts-ignore */}
                    <span>Most in-demand skills for senior managers: {getMostFrequentSkillsByRole('Senior Manager')[0]}</span></li>
                <li className="list-group-item"><span>Office city with the most jobs from the top client:  </span></li>
                <li className="list-group-item"><span>Percentage of jobs in Low Tech: {isSuccess && getPercentageOfJobsByIndustry("Low technology") + '%'}</span></li>
            </ul>
        </div>

    )
}
